import React from 'react';
import ReactDOM from 'react-dom';
import { HashRouter as Router, Route, Switch, Link } from "react-router-dom";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quest: "None",
      stu_ans: "None",
      ref_ans: "None",
      model: "None"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  componentDidMount(){
    var obj = ReactDOM.findDOMNode(this.refs.grade);
    alert(obj);
  }

  get_result(event) {
    event.preventDefault();
    const data = new FormData(event.target);

    fetch("https://asag-api.herokuapp.com/", {
      method: 'POST',
      body: data,
    }).then(results => {
      return results.json();
    }).then(data => {
      let grades = data.results;
      alert(grades);
    });
  }

  render() {
    return (
      <form onSubmit={this.get_result}>
        <label>
          Question: &nbsp;&nbsp;
          <input
            name="quest"
            type="text"
            checked={this.state.quest}
            onChange={this.handleInputChange} />
        </label>
        <br /><br />
        <label>
          Reference Answer:&nbsp;&nbsp;
          <input
            name="ref_ans"
            type="text"
            checked={this.state.ref_ans}
            onChange={this.handleInputChange} />
        </label>
        <br /><br />
        <label>
          Student Answer:&nbsp;&nbsp;
          <input
            name="stu_ans"
            type="text"
            checked={this.state.stu_ans}
            onChange={this.handleInputChange} />
        </label>
        <br /><br />
        <label>
          Model:&nbsp;&nbsp;
          <input
          type="radio"
          name="model"
          value="classifier"
          checked={this.state.model}
          onChange={this.handleInputChange} /> Classifier
          <input
          type="radio"
          name="model"
          value="regressor"
          checked={this.state.model}
          onChange={this.handleInputChange} /> Regressor
        </label>
        <br /><br />
        <label>
          Grade:&nbsp;&nbsp;
          <div ref="grade"/>
        </label>
        <br /><br />
        <label>
        Get Grade &nbsp;&nbsp;
          <input
            value="Submit"
            type="submit"
            checked={this.state.model}
            onChange={this.handleInputChange}
            onClick={this.get_result}/>
        </label>
      </form>
    );
  }
}

export default Form
