import React from 'react';
import axios from 'axios';

class UncontrolledForm extends React.Component {
  state = {
    grade: "None"
  }
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    var data = new FormData(event.target);

    axios({
      url: 'https://asag-api.herokuapp.com/',
      method: 'post',
      data: data,
      })
      .then(function (response) {
        var grade = response.data.grade;
        alert(grade);
        this.state.grade = grade;
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Question : &nbsp;&nbsp;
            <input type="text" ref={(quest) => this.quest = quest} />
          </label>
          <br/><br/>
          <label>
            Reference Answer : &nbsp;&nbsp;
            <input type="text" ref={(ref_ans) => this.ref_ans = ref_ans} />
          </label>
          <br/><br/>
          <label>
            Student Answer : &nbsp;&nbsp;
            <input type="text" ref={(stu_ans) => this.stu_ans = stu_ans} />
          </label>
          <br/><br/>
          <label>
            Model : &nbsp;&nbsp;
            <input type="radio" value="model" checked="True" ref={(model) => this.model = model}/> Classifier
            <input type="radio" value="model" ref={(model) => this.model = model}/> Regressor
          </label>
          <br/><br/>
          <input type="submit" value="Submit" />
        </form>
        <br/><br/><hr/><br/><br/>
        <div>Grade : &nbsp;&nbsp;{this.state.grade}</div>
      </div>
    );
  }
}

export default UncontrolledForm;
