import React from 'react';
import axios from 'axios';

class UncontrolledForm extends React.Component {
  state = {
    grade: "None"
  }
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    var formdata = new FormData(event.target);
    var data = {
      'quest': this.quest.value,
      'ref_ans': this.ref_ans.value,
      'stu_ans': this.stu_ans.value,
      'model': this.model.value};

    axios({
      url: 'https://asag-api.herokuapp.com/',
      method: 'post',
      data: data,
      })
      .then(function (response) {
        var grade = response.data.grade;
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
    event.preventDefault();
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Question : &nbsp;&nbsp;
            <input type="text" ref={(quest) => this.quest = quest} />
          </label>
          <br/><br/>
          <label>
            Reference Answer : &nbsp;&nbsp;
            <input type="text" ref={(ref_ans) => this.ref_ans = ref_ans} />
          </label>
          <br/><br/>
          <label>
            Student Answer : &nbsp;&nbsp;
            <input type="text" ref={(stu_ans) => this.stu_ans = stu_ans} />
          </label>
          <br/><br/>
          <label>
            Model : &nbsp;&nbsp;
            <input type="radio" value="classifier" ref={(model) => this.model = model}/> Classifier
            <input type="radio" value="regressor" ref={(model) => this.model = model}/> Regressor
          </label>
          <br/><br/>
          <input type="submit" value="Submit" />
        </form>
        <br/><br/><hr/><br/><br/>
        <form>
        Grade : &nbsp;&nbsp;
          <input type="text" name="grade" value={this.state.grade}/>
        </form>
      </div>
    );
  }
}

export default UncontrolledForm;
