import React from 'react';
import ReactDOM from 'react-dom';
import { HashRouter as Router, Route, Switch, Link } from "react-router-dom";

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      quest: "None",
      stu_ans: "None",
      ref_ans: "None",
      model: "None"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  componentDidMount(){
    const obj = ReactDOM.findDOMNode(this.refs.grade);
  }

  get_result(event) {
    event.preventDefault();
    const data = new FormData(event.target);

    alert(data);
    fetch("https://asag-api.herokuapp.com/", {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {'Content-Type': 'application/json'}
    }).then(results => {
      alert(results);
      return results.json();
    }).then(data => {
      let grades = data.results;
      alert(grades);
      // document.getElementById("grade").setInnerHTML(grades);
    });
  }

  render() {
    return (
      <div class="container">
        <form action="https://asag-api.herokuapp.com/" method="POST">
          <label>
            Question: &nbsp;&nbsp;
            <input
              name="quest"
              type="text"
              checked={this.state.quest}
              onChange={this.handleInputChange} />
          </label>
          <br /><br />
          <label>
            Reference Answer:&nbsp;&nbsp;
            <input
              name="ref_ans"
              type="text"
              checked={this.state.ref_ans}
              onChange={this.handleInputChange} />
          </label>
          <br /><br />
          <label>
            Student Answer:&nbsp;&nbsp;
            <input
              name="stu_ans"
              type="text"
              checked={this.state.stu_ans}
              onChange={this.handleInputChange} />
          </label>
          <br /><br />
          <label>
            Model:&nbsp;&nbsp;
            <input
            type="radio"
            name="model"
            value="classifier"
            checked={this.state.model}
            onChange={this.handleInputChange} /> Classifier
            <input
            type="radio"
            name="model"
            value="regressor"
            checked={this.state.model}
            onChange={this.handleInputChange} /> Regressor
          </label>
          <br /><br />
          <label>
          Get Grade &nbsp;&nbsp;
            <input
              value="Submit"
              type="submit"
              checked={this.state.model}
              onChange={this.handleInputChange}/>
          </label>
        </form>
        <br/><br/><br/>
        <hr/>
        <br/><br/>
        Grade:&nbsp;&nbsp;
        <div id="grade"> </div>
        <br /><br />
      </div>
    );
  }
}

export default Form
